// This file loads the PDF.js worker from unpkg.com
// It's a workaround for CORS issues with the PDF.js worker

// The version should match the one used in the application
const pdfjsVersion = '4.8.69';
const workerUrl = `https://unpkg.com/pdfjs-dist@${pdfjsVersion}/build/pdf.worker.min.js`;

// Create a script element to load the worker
const script = document.createElement('script');
script.src = workerUrl;
script.async = true;
document.head.appendChild(script);

// Redirect to the worker
self.location = workerUrl; 